AND (ALLTAG LIKE '%${search}%' OR TITLE LIKE '%${search}%' OR TAG LIKE '%${search}%')
AND CODE = #{code}
ORDER BY NO ASC LIMIT ${limit} OFFSET ${offset}

SELECT COUNT(*) FROM VISIT_JEJU V WHERE 1=1
AND (ALLTAG LIKE '%${search}%' OR TITLE LIKE '%${search}%' OR TAG LIKE '%${search}%')
AND CODE = #{code}
